query:
fragment BasicProductFields on Product {
  id
  name
  thumbnail {
    url
    alt
    __typename
  }
  thumbnail2x: thumbnail(size: 510) {
    url
    __typename
  }
  __typename
}

query Category($id: ID!, $attributes: [AttributeScalar], $after: String, $pageSize: Int, $sortBy: ProductOrder, $priceLte: Float, $priceGte: Float) {
  products(after: $after, attributes: $attributes, categories: [$id], first: $pageSize, sortBy: $sortBy, priceLte: $priceLte, priceGte: $priceGte) {
    totalCount
    edges {
      node {
        ...BasicProductFields
        price {
          amount
          currency
          localized
          __typename
        }
        category {
          id
          name
          __typename
        }
        __typename
      }
      __typename
    }
    pageInfo {
      endCursor
      hasNextPage
      hasPreviousPage
      startCursor
      __typename
    }
    __typename
  }
  category(id: $id) {
    seoDescription
    seoTitle
    id
    name
    backgroundImage {
      url
      __typename
    }
    ancestors(last: 5) {
      edges {
        node {
          id
          name
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
  attributes(inCategory: $id, first: 100) {
    edges {
      node {
        id
        name
        slug
        values {
          id
          name
          slug
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
}
